mapper.select:
SELECT "subGyomuCode" ,"shichosonCode" ,"shoriName" ,"shoriEdaban" ,"nendo" ,"nendoNaiRenban" ,"kijunYMD" ,"kijunTimestamp" ,"taishoKaishiYMD" ,"taishoShuryoYMD" ,"taishoKaishiTimestamp" ,"taishoShuryoTimestamp" FROM rgdb."DbT7022ShoriDateKanri" as DbT7022 WHERE DbT7022."subGyomuCode" = 'DBA' AND DbT7022."shoriName" = '広域住基連動' AND DbT7022.nendo = '0000' AND
DbT7022."shichosonCode" = #{item}
AND DbT7022."shoriEdaban" = (SELECT MAX(A."shoriEdaban") FROM rgdb."DbT7022ShoriDateKanri" as A WHERE A."shoriName" = DbT7022."shoriName" AND A.nendo = DbT7022.nendo) AND DbT7022."nendoNaiRenban" = (SELECT MAX(B."nendoNaiRenban") FROM rgdb."DbT7022ShoriDateKanri" as B WHERE B."shoriName" = DbT7022."shoriName" AND B.nendo = DbT7022.nendo)
