mapper.select:
SELECT , , , ,
FROM rgdb."DbV2002Fuka" AS "DbV2002Fuka" INNER JOIN rgdb."DbT2003Kibetsu" AS "DbT2003Kibetsu" ON "DbT2003Kibetsu"."choteiNendo" = "DbV2002Fuka"."choteiNendo" AND "DbT2003Kibetsu"."fukaNendo" = "DbV2002Fuka"."fukaNendo" AND "DbT2003Kibetsu"."tsuchishoNo" = "DbV2002Fuka"."tsuchishoNo" AND "DbT2003Kibetsu"."rirekiNo" = "DbV2002Fuka"."rirekiNo" INNER JOIN rgdb."UrT0705ChoteiKyotsu" AS "UrT0705ChoteiKyotsu" ON "DbT2003Kibetsu"."choteiId" = "UrT0705ChoteiKyotsu"."choteiId" INNER JOIN rgdb."DbV2001ChoshuHoho" AS "DbV2001ChoshuHoho" ON "DbV2001ChoshuHoho"."fukaNendo" = "DbV2002Fuka"."fukaNendo" AND "DbV2001ChoshuHoho"."hihokenshaNo" = "DbV2002Fuka"."hihokenshaNo" AND "DbV2001ChoshuHoho"."rirekiNo" = "DbV2002Fuka"."rirekiNo" LEFT JOIN rgca.${psmTotalShunyu} AS "TotalShunyu" ON "UrT0705ChoteiKyotsu"."shunoId" = "TotalShunyu"."UrT0700shunoId" WHERE "DbV2002Fuka"."choteiNendo"= #{choteiNendo} AND "DbV2002Fuka"."fukaNendo" = #{fukaNendo}
ORDER BY "DbV2002Fuka"."choteiNendo", "DbV2002Fuka"."fukaNendo", "DbV2002Fuka"."tsuchishoNo", "DbT2003Kibetsu"."ki"
