mapper.sql:
select count(*) from ( SELECT B. "hihokenshaNo", B. "taishoYM", B. "rirekiNo" FROM rgdb. "DbT3005KyotakuKeikakuTodokede" AS B INNER JOIN ( SELECT "hihokenshaNo" ,MAX ("taishoYM" || "rirekiNo") AS "max1" FROM rgdb. "DbT3005KyotakuKeikakuTodokede" GROUP BY "hihokenshaNo" , "taishoYM" ) AS A ON A. "hihokenshaNo" = B. "hihokenshaNo" AND A. "max1" = (B. "taishoYM" || B. "rirekiNo") ) DbT3005
LEFT OUTER JOIN rgdb."DbT3006KyotakuKeikakuJigyoshaSakusei" AS DbT3006 ON DbT3005."hihokenshaNo" = DbT3006."hihokenshaNo" AND DbT3005."taishoYM" = DbT3006."taishoYM" AND DbT3005."rirekiNo" = DbT3006."rirekiNo"
where 1=1 AND DbT3005."hihokenshaNo" = #{被保険者番号} AND DbT3005."taishoYM" > #{対象年月} AND DbT3005."taishoYM" || '01' <= #{基準年月日}
