SELECT shinsei.*, T2."識別コード", T2."氏名" FROM () AS shinsei INNER JOIN (SELECT kihon."hiHokenshaNo", kihon."serviceTeikyoYM", kihon."seiriNo" FROM rgdb."DbT3038ShokanKihon" kihon INNER JOIN rgdb."DbT3053ShokanShukei" shukei ON shukei."hiHokenshaNo" = kihon."hiHokenshaNo" AND shukei."serviceTeikyoYM" = kihon."serviceTeikyoYM" AND shukei."seiriNo" = kihon."seiriNo" AND shukei."jigyoshaNo" = kihon."jigyoshaNo" AND shukei."yoshikiNo" = kihon."yoshikiNo" AND shukei."meisaiNo" = kihon."meisaiNo" AND SUBSTRING(shukei."yoshikiNo",1,3) = '21D' GROUP BY kihon."hiHokenshaNo", kihon."serviceTeikyoYM", kihon."seiriNo" ) AS T1 ON T1."hiHokenshaNo" = shinsei."dbT3034ShokanShinsei_hiHokenshaNo" AND T1."serviceTeikyoYM" = shinsei."dbT3034ShokanShinsei_serviceTeikyoYM" AND T1."seiriNo" = shinsei."dbT3034ShokanShinsei_seiriNo" INNER JOIN (SELECT daicho."hihokenshaNo" AS "hihokenshaNo", daicho."shikibetsuCode" AS "識別コード", 宛名."meisho" AS "氏名" FROM rgdb."DbT1001HihokenshaDaicho" daicho INNER JOIN
AS 宛名 ON 宛名."shikibetsuCode" = daicho."shikibetsuCode" WHERE daicho."logicalDeletedFlag" <> TRUE GROUP BY daicho."hihokenshaNo", daicho."shikibetsuCode", 宛名."meisho" ) AS T2 ON T2."hihokenshaNo" = shinsei."dbT3034ShokanShinsei_hiHokenshaNo" WHERE shinsei."dbT3034ShokanShinsei_shikyuShinseiShinsaKubun" = '1' AND shinsei."dbT3034ShokanShinsei_shinsaHohoKubun" = '2'
AND shinsei."dbT3034ShokanShinsei_shinseiYMD" >= #{支給申請開始日}
AND shinsei."dbT3034ShokanShinsei_shinseiYMD" <= #{支給申請終了日}
ORDER BY shinsei."dbT3034ShokanShinsei_shinseiYMD" ASC, shinsei."dbT3034ShokanShinsei_serviceTeikyoYM" ASC
