mapper.select:
WITH T AS(
SELECT  A.*
FROM rgdb."DbT3068KogakuGassanShinseisho" AS A
WHERE A."isDeleted" = FALSE

AND A."shinseiJokyoKubun" = #{申請状況区分}


AND A."hokenshaNo" = #{市町村コード}


AND A."taishoNendo" = #{申請対象年度}


AND A."shinseiYMD" = #{申請日}


AND A."shikyuShinseishoSeiriNo" = #{介護支給申請書整理番号}


AND A."kokuhoShikyuShinseishoSeiriNo" = #{医療支給申請書整理番号}



        AND A."shinseiDaihyoshaShimei" LIKE #{申請代表者氏名} || '%'


        AND A."shinseiDaihyoshaShimei" = #{申請代表者氏名}



    AND A."hihokenshaNo" = #{被保険者番号}

        AND A."hihokenshaNo" IN 

        #{被保険者番号リスト}



)
SELECT M.*
FROM T AS M
WHERE M."rirekiNo" = (   
        SELECT MAX(N."rirekiNo")
        FROM T AS N
        WHERE M."hihokenshaNo" = N."hihokenshaNo"
        AND M."taishoNendo" = N."taishoNendo"
        AND M."hokenshaNo" = N."hokenshaNo"
        AND M."seiriNo" = N."seiriNo")
ORDER BY M."taishoNendo" DESC,
         M."hihokenshaNo" DESC,
         M."seiriNo" DESC,
         M."shinseiYMD" DESC

WITH hihokenshaDaicho AS ( SELECT DbV1001."shikibetsuCode" FROM rgdb."DbV1001HihokenshaDaicho" as DbV1001 WHERE DbV1001."hihokenshaNo" = #{被保険者番号} ) SELECT
FROM hihokenshaDaicho LEFT OUTER JOIN
( SELECT ARRAY_AGG(CAST ("shikibetsuCode" AS TEXT)) FROM hihokenshaDaicho ) AS "ShikibetsuTaisho" ON hihokenshaDaicho."shikibetsuCode" = "ShikibetsuTaisho"."shikibetsuCode";

SELECT  A.*
FROM rgdb."DbT3069KogakuGassanShinseishoKanyureki" AS A
WHERE 
A."taishoNendo" = #{対象年度}
AND A."hokenshaNo" = #{保険者番号}
AND A."seiriNo" = #{整理番号}
AND A."hihokenshaNo" = #{被保険者番号}
AND A."rirekiNo" = #{履歴番号}
ORDER BY A."kanyurekiNo" DESC
FETCH FIRST 1 ROWS ONLY
