  "jigyoshaNo" = #{事業者番号}
  AND "serviceShuruiCode" = #{サービス種類コード} 
  AND "yukoKaishiYMD" <= #{サービス提供年月の月初日} 
  AND ( 
    #{サービス提供年月の月初日} <= "yukoShuryoYMD" 
