mapper.select:
SELECT T3067."shokisaiHokenshaNo" AS "shokisaiHokenshaNo", T3067."serviceTeikyoYM" AS "serviceTeikyoYM", T3067."jigyoshoNo" AS "jigyoshoNo", T7060."jigyoshaName" AS "jigyoshaName", T3067."serviceShuruiCode" AS "serviceShuruiCode", T7130."serviceShuruiRyakusho" AS "serviceShuruiRyakusho", T3067."riyoshaFutangaku" AS "riyoshaFutangaku", T3067."serviceHiyoTotal" AS "serviceHiyoTotal" FROM rgdb."DbT3067KyufuhiTuchiHosei" AS T3067 INNER JOIN rgdb."DbT7060KaigoJigyosha" AS T7060 ON T3067."jigyoshoNo" = T7060."jigyoshaNo" INNER JOIN rgdb."DbT7130KaigoServiceShurui" AS T7130 ON T3067."serviceShuruiCode" = T7130."serviceShuruiCd" WHERE T3067."hiHokenshaNo"=#{hiHokenshaNo}
AND #{serviceTeikyoKaisiYM} <= T3067."serviceTeikyoYM"
AND T3067."serviceTeikyoYM" <= #{serviceTeikyoSyuryoYM}
LIMIT #{maxCount}

mapper.update:
UPDATE rgdb."DbT3067KyufuhiTuchiHosei"
SET
  "shokisaiHokenshaNo" = #{shokisaiHokenshaNo},
  "serviceTeikyoYM" = #{serviceTeikyoYM},
  "jigyoshoNo" = #{jigyoshoNo},
  "serviceShuruiCode" = #{serviceShuruiCode},
  "riyoshaFutangaku" = #{riyoshaFutangaku},
  "serviceHiyoTotal" = #{serviceHiyoTotal}
WHERE
    "hiHokenshaNo" = #{hiHokenshaNo}
AND "shokisaiHokenshaNo" = #{shokisaiHokenshaNo}
AND "serviceTeikyoYM" = #{serviceTeikyoYM}
AND "jigyoshoNo" = #{jigyoshoNo}
AND "serviceShuruiCode" = #{serviceShuruiCode}

mapper.delete:
DELETE FROM rgdb."DbT3067KyufuhiTuchiHosei" AS DbT3067
WHERE
     DbT3067."hiHokenshaNo" = #{hiHokenshaNo}
AND  DbT3067."shokisaiHokenshaNo" = #{shokisaiHokenshaNo}
AND  DbT3067."serviceTeikyoYM" = #{serviceTeikyoYM}
AND  DbT3067."jigyoshoNo" = #{jigyoshoNo}
AND  DbT3067."serviceShuruiCode" = #{serviceShuruiCode}
