mapper.insert:
INSERT INTO "TABLE_SHOKANFUSHIKYUSINHIHOKENSHABANGOICHIJI"
VALUES (
    #{市町村コード},
    #{新番号},
    #{旧番号}
)

mapper.update:
UPDATE "DbTShokanFushikyuKetteiInTempTable"
SET     "jushoCode" = #{全国住所コード},
        "jusho" = #{住所},
        "gyoseikuCode" = #{行政区コード},
        "gyoseiku" = #{行政区名}
WHERE "shikibetsuCode" = #{識別コード}

mapper.select:
SELECT TEMP."bikoOne" AS bikoOne,
       TEMP."bikoTwo" AS bikoTwo
FROM "DbTShokanFushikyuKetteiInTempTable" AS TEMP
WHERE TEMP."hiHokenshaNo" = #{hiHokenshaNo}
AND   TEMP."serviceTeikyoYM" = #{serviceTeikyoYM}
AND   TEMP."seiriNo" = #{seiriNo}
AND   TEMP."jigyoshoNo" = #{jigyoshoNo}
AND   TEMP."serviceShuruiCode" = #{serviceShuruiCode}
AND   TEMP."shikyuKubunCode" = #{shikyuKubunCode}
LIMIT(1)
