mapper.select:
WITH T AS (SELECT 再審査申立."jigyoshoNo" ,介護事業者."jigyoshaName" ,再審査申立."hiHokenshaNo" ,再審査申立."serviceTeikyoYM" ,再審査申立."serviceTeikyoShuruiCode" ,再審査申立."serviceKomokuCode" ,介護サービス内容."serviceNameRyaku" ,再審査申立."shokisaiHokenshaNo" ,再審査申立."moshitateYMD" ,再審査申立."moshitateshaKubunCode" ,再審査申立."moshitateTanisu" ,再審査申立."moshitateJiyuCode" ,dbv1001."shikibetsuCode" FROM rgdb."DbT3062SaishinsaMoshitate" AS 再審査申立 LEFT JOIN rgdb."DbT7060KaigoJigyosha" AS 介護事業者 ON 再審査申立."jigyoshoNo" = 介護事業者."jigyoshaNo" AND 介護事業者."yukoKaishiYMD" = (SELECT MAX("yukoKaishiYMD") FROM rgdb."DbT7060KaigoJigyosha" WHERE "jigyoshaNo" = 再審査申立."jigyoshoNo") LEFT JOIN rgdb."DbV1001HihokenshaDaicho" AS dbv1001 ON dbv1001."hihokenshaNo" = 再審査申立."hiHokenshaNo" LEFT JOIN rgdb."DbT7131KaigoServiceNaiyou" AS 介護サービス内容 ON 再審査申立."serviceTeikyoShuruiCode" = 介護サービス内容."serviceShuruiCd" AND 再審査申立."serviceKomokuCode" = 介護サービス内容."serviceKoumokuCd" AND 介護サービス内容."teikyoKaishiYM" <= 再審査申立."serviceTeikyoYM" AND 再審査申立."serviceTeikyoYM" <= 介護サービス内容."teikyoShuryoYM" AND 介護サービス内容."rirekiNo" = (SELECT MAX("rirekiNo") FROM rgdb."DbT7131KaigoServiceNaiyou" WHERE 再審査申立."serviceTeikyoShuruiCode" = "serviceShuruiCd" AND 再審査申立."serviceKomokuCode" = "serviceKoumokuCd" AND "teikyoKaishiYM" <= 再審査申立."serviceTeikyoYM" AND 再審査申立."serviceTeikyoYM" <= "teikyoShuryoYM") WHERE
再審査申立."kokuhorenSofuYM" = #{送付年月}
再審査申立."kokuhorenSofuYM" IS NULL
AND substr(再審査申立."shokisaiHokenshaNo",1,5) = #{証記載保険者番号}
ORDER BY 再審査申立."hiHokenshaNo",再審査申立."serviceTeikyoYM", 再審査申立."jigyoshoNo",再審査申立."serviceTeikyoShuruiCode",再審査申立."rirekiNo" DESC) SELECT T."jigyoshoNo" AS 事業所番号 ,T."jigyoshaName" AS 事業所名 ,T."hiHokenshaNo" AS 被保険者番号 ,T."serviceTeikyoYM" AS サービス提供年月 ,T."serviceTeikyoShuruiCode" AS サービス種類コード ,T."serviceKomokuCode" AS サービス項目コード ,T."serviceNameRyaku" AS サービス略称 ,T."shokisaiHokenshaNo" AS 証記載保険者番号 ,T."moshitateYMD" AS 申立年月日 ,T."moshitateshaKubunCode" AS 申立者区分コード ,T."moshitateTanisu" AS 申立単位数 ,T."moshitateJiyuCode" AS 申立事由コード ,宛名."meisho" AS 宛名名称 FROM T LEFT JOIN rgua."UaFt200FindShikibetsuTaisho"('','',FALSE,'DB',FALSE,'','','','','','','','','','','',TRUE, '','','','','','','',FALSE,'','','','','','','','','','','',FALSE,FALSE,FALSE,FALSE,FALSE,FALSE,FALSE,FALSE,FALSE,FALSE, FALSE,'','','','','','','','',FALSE,'','',FALSE,FALSE,FALSE,'','','','','','','','','','','','','','','','','','','{}') AS 宛名 ON 宛名."shikibetsuCode" = T."shikibetsuCode"
