SELECT T4101."koroshoIfShikibetsuCode" AS 厚労省IF識別コード, T4003."hihokenshaNo" AS 被保番号, T4101."hihokenshaName" AS 氏名, T4101."hihokenshaKana" AS 氏名カナ, T4101."jusho" AS 住所, T4003."shinseiYMD" AS 申請日, T4101."enkiKetteiYMD" AS 延期決定年月日, T4101."enkiRiyu" AS 延期理由, T4101."enkiTsuchiHakkoYMD" AS 延期通知発行年月日, T4101."enkiTsuchiHakkoKaisu" AS 延期通知発行回数, T4101."enkiMikomiKaishiYMD" AS 延期見込期間開始年月日, T4101."enkiMikomiShuryoYMD" AS 延期見込期間終了年月日, T7051."shichosonCode" AS 市町村コード, T7051."shichosonMeisho" AS 市町村名称 FROM rgdb."DbT4003YokaigoNinteiInterface" AS T4003 INNER JOIN rgdb."DbT4001JukyushaDaicho" AS T4001 ON T4001."hihokenshaNo" = T4003."hihokenshaNo" AND T4001."shinseishoKanriNo" = T4003."shinseishoKanriNo" AND T4001."rirekiNo" = T4003."rirekiNo" AND T4001."logicalDeletedFlag" = FALSE INNER JOIN rgdb."DbT4101NinteiShinseiJoho" AS T4101 ON T4001."shinseishoKanriNo" = T4101."shinseishoKanriNo" AND T4101."logicalDeletedFlag" = FALSE LEFT JOIN rgdb."DbT7051KoseiShichosonMaster" AS T7051 ON T4101."shoKisaiHokenshaNo" = T7051."shoKisaiHokenshaNo" WHERE
T4101."enkiTsuchiHakkoYMD" = #{tsuchishohakkonbi}
T4101."enkiTsuchiHakkoYMD" IS NULL
AND #{shorimikomibiFrom} <= T4101."enkiKetteiYMD"
AND T4101."enkiKetteiYMD" <= #{shorimikomibiTo}
ORDER BY T7051."shichosonCode", T4101."hihokenshaKana", T4101."ninteiShinseiYMD"

SELECT T4101."koroshoIfShikibetsuCode" AS 厚労省IF識別コード, T4101."hihokenshaNo" AS 被保番号, T4101."hihokenshaName" AS 氏名, T4101."hihokenshaKana" AS 氏名カナ, T4101."jusho" AS 住所, T4101."ninteiShinseiYMD" AS 申請日, T4101."enkiKetteiYMD" AS 延期決定年月日, T4101."enkiRiyu" AS 延期理由, T4101."enkiTsuchiHakkoYMD" AS 延期通知発行年月日, T4101."enkiTsuchiHakkoKaisu" AS 延期通知発行回数, T4101."enkiMikomiKaishiYMD" AS 延期見込期間開始年月日, T4101."enkiMikomiShuryoYMD" AS 延期見込期間終了年月日, T7051."shichosonCode" AS 市町村コード, T7051."shichosonMeisho" AS 市町村名称 FROM rgdb."DbT4101NinteiShinseiJoho" AS T4101 LEFT JOIN rgdb."DbT7051KoseiShichosonMaster" AS T7051 ON T4101."shoKisaiHokenshaNo" = T7051."shoKisaiHokenshaNo" LEFT JOIN rgdb."DbT4116IchijiHanteiKekkaJoho" AS T4116 ON T4101."shinseishoKanriNo" = T4116."shinseishoKanriNo" LEFT JOIN rgdb."DbT5510IchiGojiHanteiKekkaJoho" AS T5510 ON T4101."shinseishoKanriNo" = T5510."shinseishoKanriNo" LEFT JOIN rgdb."DbT5502ShinsakaiWariateJoho" AS T5502 ON T4101."shinseishoKanriNo" = T5502."shinseishoKanriNo" LEFT JOIN rgdb."DbT5105NinteiKanryoJoho" AS T5105 ON T5105."shinseishoKanriNo" = T4101."shinseishoKanriNo" WHERE
T4101."enkiTsuchiHakkoYMD" = #{tsuchishohakkonbi}
T4101."enkiTsuchiHakkoYMD" IS NULL
AND #{shorimikomibiFrom} <= T4101."enkiKetteiYMD"
AND T4101."enkiKetteiYMD" <= #{shorimikomibiTo}
AND T4101."shinseishoKanriNo" NOT IN( SELECT rgdb."DbT4102NinteiKekkaJoho"."shinseishoKanriNo" FROM rgdb."DbT4102NinteiKekkaJoho" ) AND T4101."logicalDeletedFlag" = FALSE AND T5105."ninteiShinseiJohoTorokuKanryoYMD" IS NOT NULL AND T5105."ninteiShinsakaiKanryoYMD" IS NULL ORDER BY T7051."shichosonCode", T4101."hihokenshaKana", T4101."ninteiShinseiYMD"

SELECT A.* FROM rgdb."DbT4101NinteiShinseiJoho" AS A WHERE A."shinseishoKanriNo" IN
#{shinseishokanribangorisuto}
AND A."logicalDeletedFlag" = False ORDER BY A."hihokenshaKana", A."ninteiShinseiYMD"
