SELECT C."hihokenshaNo" AS 被保険者番号, C."shikibetsuCode" AS 識別コード, F."kyuSochishaFlag" AS 旧措置者フラグ, E."shichosonCode" AS 市町村コード, E."koikinaiTokureiSochimotoShichosonCode" AS 広住特措置元市町村コード,
FROM rgdb."DbT4032ShinseishoIkkatsuHakkoBatch" AS A INNER JOIN rgdb."DbT4033ShinseishoHakkoTaishoshas" AS B ON B."hakkoShoriID" = A."hakkoShoriID" INNER JOIN rgdb."DbT4031ShinseishoHakkoKohoshas" AS C ON C."haakuShoriID" = A."haakuShoriId" AND C."hihokenshaNo" = B."hihokenshaNo" INNER JOIN rgua."UaFt200FindShikibetsuTaisho"('',#{基準日},FALSE,'DB',FALSE,'','','','','','','','','','','',true, '','','','','','','',true,'','','','','','','','','','','',true,true,true,true,true,true,true,true,true,true, true,'','','','','','','','',true,'','',true,true,true,'','','','','','','','','','','','','','','','','','','{}') AS "ShikibetsuTaisho" ON "ShikibetsuTaisho"."shikibetsuCode" =C."shikibetsuCode" INNER JOIN rgdb."DbV1001HihokenshaDaicho" AS E ON E."hihokenshaNo" =C."hihokenshaNo" LEFT JOIN rgdb."DbV4001JukyushaDaicho" AS F ON F."hihokenshaNo" =C."hihokenshaNo" AND F."ninteiYMD" <=#{基準日} AND F."logicalDeletedFlag" IS FALSE WHERE A."hakkoShoriID" =#{発行処理ID}
ORDER BY C."willBeRenewed",
