mapper.select:
SELECT A."updateCount" AS count, A.hihokenshaNo AS 被保険者番号, A."dtkanashimei" AS カナ氏名, A."dtkanjishimei" AS 漢字氏名, A."dtbirthday" AS 生年月日, A."dtseibetsu" AS 性別, A."dtnenkinhokenshacode" AS 年金保険者, A."dtkisonenkinno" AS 基礎年金番号, A."genkisonenkinno" AS 現基礎年金番号, A."dtnenkincode" AS 年金コード, A."dttaishoy" AS 対象年, A."dtsakuseiymd" AS 作成年月日, A."shorikubun" AS 通知内容, A."torokukubun" AS 登録区分, A."dtkanajusho" AS 住所カナ, A."dtkanjijusho" AS 住所漢字, A."dtkingaku1" AS 金額 FROM rgdb."DbT4037HikazeNenkinTaishosha" AS A WHERE '1' = '1'
AND A."nendo" = #{年度}
AND A."dttaishoy" = #{対象年}
AND A."hihokenshano" = #{被保険者番号}
AND A."dtnenkinhokenshacode" = #{年金保険者コード}
AND A."kisonenkinno" = #{基礎年金番号}
AND A."nenkincode" = #{年金コード}
ORDER BY A."dttaishoy" DESC, A."dtnenkinhokenshacode" ASC, A."genkisonenkinno" ASC, subString(A."nenkincode",1,3) ASC, A."dtsakuseiymd" DESC

mapper.update:
UPDATE
rgdb."DbT4037HikazeNenkinTaishosha"
SET
"genkisonenkinno" = #{現基礎年金番号},
"hihokenshano" = #{被保険者番号}
WHERE
"kisonenkinno" = #{基礎年金番号_キー}
AND "nenkincode" = #{年金コード_キー}
AND "hihokenshano" = #{被保番号_キー}
AND "dtnenkinhokenshacode" = #{年金保険者コード_キー}
AND "dtsakuseiymd" = #{作成年月日_キー}
AND "dttaishoy" = #{対象年_キー}

UPDATE
rgdb."DbT4037HikazeNenkinTaishosha"
SET
"shorikubun" = #{処理区分},
"taishom" = #{対象月},
"genkisonenkinno" = #{現基礎年金番号},
"hihokenshano" = #{被保険者番号},
"dtsakuseiymd" = #{dt作成年月日},
"dtseibetsu" = #{dt性別},
"dtbirthday" = #{dt生年月日},
"dtkanashimei" = #{dtカナ氏名},
"dtkanjishimei" = #{dt漢字氏名},
"dtkanajusho" = #{dtカナ住所},
"dtkanjijusho" = #{dt漢字住所},
"dttaishoy" = #{dt対象年},
"dtkingaku1" = #{dt金額１}
WHERE
"kisonenkinno" = #{基礎年金番号_キー}
AND "nenkincode" = #{年金コード_キー}
AND "hihokenshano" = #{被保番号_キー}
AND "dtnenkinhokenshacode" = #{年金保険者コード_キー}
AND "dtsakuseiymd" = #{作成年月日_キー}
AND "dttaishoy" = #{対象年_キー}

mapper.delete:
DELETE FROM
rgdb."DbT4037HikazeNenkinTaishosha"
WHERE
"kisonenkinno" = #{基礎年金番号_キー}
AND "nenkincode" = #{年金コード_キー}
AND "hihokenshano" = #{被保番号_キー}
AND "dtnenkinhokenshacode" = #{年金保険者コード_キー}
AND "dtsakuseiymd" = #{作成年月日_キー}
AND "dttaishoy" = #{対象年_キー}
