mapper.select:
SELECT A."shinseishoKanriNo" AS 申請書管理番号, A."hihokenshaNo" AS 被保番号, A."ninteiShinseiYMD" AS 認定申請年月日, A."ninteiShinseiShinseijiKubunCode" AS 申請区分_申請時_コード, A."hihokenshaName" AS 氏名, A."hihokenshaKana" AS 氏名カナ, A."enkiKetteiYMD" AS 延期決定日, A."enkiRiyu" AS 延期理由, A."enkiTsuchiHakkoYMD" AS 通知書発行日, A."enkiTsuchiHakkoKaisu" AS 延期回数, A."enkiMikomiKaishiYMD" AS 延期見込み期間開始日, A."enkiMikomiShuryoYMD" AS 延期見込み期間終了日, B."ninteichosaJisshiYMD" AS 調査実施日, C."ikenshoJuryoYMD" AS 意見書受領日, D."ichijiHanteiYMD" AS 一次判定日, E."shinsakaiKaisaiYMD" AS 審査会予定日, G."shichosonMeisho" AS 保険者名 FROM rgdb."DbT4101NinteiShinseiJoho" AS A LEFT JOIN rgdb."DbT4202NinteichosahyoGaikyoChosa" B ON A."shinseishoKanriNo" = B."shinseishoKanriNo" AND B."ninteichosaRirekiNo" = ( SELECT MAX("ninteichosaRirekiNo") FROM rgdb."DbT4202NinteichosahyoGaikyoChosa" B1 WHERE A."shinseishoKanriNo" = B1."shinseishoKanriNo" AND B1."isDeleted" = FALSE ) AND B."isDeleted" = FALSE LEFT JOIN rgdb."DbT4302ShujiiIkenshoJoho" C ON A."shinseishoKanriNo" = C."shinseishoKanriNo" AND C."ikenshoIraiRirekiNo" = ( SELECT MAX("ikenshoIraiRirekiNo") FROM rgdb."DbT4302ShujiiIkenshoJoho" WHERE "shinseishoKanriNo" = A."shinseishoKanriNo" AND "isDeleted" = FALSE ) AND C."isDeleted" = FALSE LEFT JOIN rgdb."DbT4116IchijiHanteiKekkaJoho" D ON A."shinseishoKanriNo" = D."shinseishoKanriNo" LEFT JOIN rgdb."DbT5502ShinsakaiWariateJoho" E ON A."shinseishoKanriNo" = E."shinseishoKanriNo" LEFT JOIN rgdb."DbT5105NinteiKanryoJoho" F ON A."shinseishoKanriNo" = F."shinseishoKanriNo" LEFT JOIN rgdb."DbT7051KoseiShichosonMaster" G ON A."shoKisaiHokenshaNo" = G."shoKisaiHokenshaNo" WHERE A."logicalDeletedFlag" = FALSE AND A."ninteiShinseiYMD" <= #{認定申請年月日} AND A."shinseishoKanriNo" NOT IN( SELECT "shinseishoKanriNo" FROM rgdb."DbT4102NinteiKekkaJoho" )
AND A."ninteiShinseiShinseijiKubunCode" = #{申請区分_新規申請}
AND A."ninteiShinseiShinseijiKubunCode" = #{申請区分_更新申請}
AND A."ninteiShinseiShinseijiKubunCode" = #{申請区分_区分変更申請}
AND (A."ninteiShinseiShinseijiKubunCode" = #{申請区分_新規申請} OR A."ninteiShinseiShinseijiKubunCode" = #{申請区分_更新申請})
AND (A."ninteiShinseiShinseijiKubunCode" = #{申請区分_新規申請} OR A."ninteiShinseiShinseijiKubunCode" = #{申請区分_更新申請})
AND (A."ninteiShinseiShinseijiKubunCode" = #{申請区分_更新申請} OR A."ninteiShinseiShinseijiKubunCode" = #{申請区分_区分変更申請})
AND (A."enkiTsuchiHakkoYMD" IS NULL OR A."enkiTsuchiHakkoYMD" = '')
AND (A."enkiTsuchiHakkoYMD" IS NOT NULL OR A."enkiTsuchiHakkoYMD" <> '')
AND A."minashiNigoEtcTaishoFlag" = #{みなし２号等対象フラグ}
AND A."enkiMikomiKaishiYMD" >= #{処理見込み日From}
AND A."enkiMikomiShuryoYMD" <= #{処理見込み日To}
AND A."enkiRiyu" = #{延期の理由}
AND G."shichosonCode" = #{市町村コード}
AND (F."ninteichosaKanryoYMD" IS NULL OR A."enkiTsuchiHakkoYMD" = '')
AND (F."ninteichosaKanryoYMD" IS NOT NULL OR A."enkiTsuchiHakkoYMD" <> '')
AND (F."ikenshoTorokuKanryoYMD" IS NULL OR A."enkiTsuchiHakkoYMD" = '')
AND (F."ikenshoTorokuKanryoYMD" IS NOT NULL OR A."enkiTsuchiHakkoYMD" <> '')
AND (F."ichijiHanteiKanryoYMD" IS NULL OR A."enkiTsuchiHakkoYMD" = '')
AND (F."ichijiHanteiKanryoYMD" IS NOT NULL OR A."enkiTsuchiHakkoYMD" <> '')
AND (F."ninteiShinsakaiWariateKanryoYMD" IS NULL OR A."enkiTsuchiHakkoYMD" = '')
AND (F."ninteiShinsakaiWariateKanryoYMD" IS NOT NULL OR A."enkiTsuchiHakkoYMD" <> '')
ORDER BY A."hihokenshaKana",A."ninteiShinseiYMD"
