mapper.select:
SELECT
A.*,A1.*,A2.*,B.* FROM (SELECT * FROM (SELECT *, row_number() OVER (PARTITION BY "hihokenshaKana" ORDER BY "ninteiShinseiYMD" DESC) AS rnbn FROM rgdb."DbT4101NinteiShinseiJoho" WHERE "logicalDeletedFlag"=false)AS Newest where rnbn = 1 ) as A INNER JOIN rgdb."DbV4001JukyushaDaicho" as B ON A."shinseishoKanriNo"=B."shinseishoKanriNo" LEFT JOIN rgdb."DbT4121ShinseiRirekiJoho" as A1 ON A."shinseishoKanriNo" =A1."shinseishoKanriNo" LEFT JOIN rgdb."DbT4101NinteiShinseiJoho" as A2 ON A1."zenkaiShinseishoKanriNo" =A2."shinseishoKanriNo" WHERE A."ninteiShinseiShinseijiKubunCode" NOT IN ('6','A','B') AND (A."koshinTsuchiHakkoYMD" is not null AND A."koshinTsuchiHakkoYMD"!='') AND (A."koshinTsuchiHakkoKanryoYMD" is not null AND A."koshinTsuchiHakkoKanryoYMD"!='') AND A."shoriJotaiKubun"=#{shoriJotaiKubun}
AND LEFT(B."ninteiYukoKikanShuryoYMD",6)=#{対象月}
AND #{有効期間終了日From}<= B."ninteiYukoKikanShuryoYMD" AND B."ninteiYukoKikanShuryoYMD" <=#{有効期間終了日To}
AND A."shinseishoKanriNo" NOT IN (SELECT rgdb."DbT4131Tenshutsu"."shinseishoKanriNo" FROM rgdb."DbT4131Tenshutsu" UNION SELECT rgdb."DbT4130Shibo"."shinseishoKanriNo" FROM rgdb."DbT4130Shibo")
