SELECT "DbT4101"."hihokenshaNo" AS "hihokenshaNo", "DbT4101"."hihokenshaName" AS "hihokenshaName", "DbT4101"."hihokenshaKana" AS "hihokenshaKana", "DbT4101"."seibetsu" AS "seibetsu", "DbT4101"."seinengappiYMD" AS "seinengappiYMD" , "DbT4101"."ninteiShinseiYMD" AS "ninteiShinseiYMD" , "DbT4101"."ninteiShinseiShinseijiKubunCode" AS "ninteiShinseiShinseijiKubunCode" FROM rgdb."DbT4101NinteiShinseiJoho" AS "DbT4101" INNER JOIN rgdb."DbT5105NinteiKanryoJoho" AS "DbT5105" ON "DbT4101"."shinseishoKanriNo" = "DbT5105"."shinseishoKanriNo" AND "DbT5105"."ninteiShinseiJohoTorokuKanryoYMD" IS NOT NULL INNER JOIN rgdb."DbT7211GaibuRenkeiDataoutputJoho" AS "DbT7211" ON "DbT4101"."shinseishoKanriNo" = "DbT7211"."shinseishoKanriNo" WHERE "DbT4101"."shoKisaiHokenshaNo" =#{shoKisaiHokenshaNo} AND "DbT4101"."logicalDeletedFlag" =#{logicalDeletedFlag} AND "DbT7211"."ninteiShinseiJohoChushutsuYMD" is NULL
AND "DbT4101"."insertTimestamp" >= #{konkaikaishiTimestamp}
AND "DbT4101"."insertTimestamp" < #{konkaisyuryoTimestamp}
AND "DbT4101"."hihokenshaNo" = #{hihokenshaNo}
ORDER BY "DbT4101"."hihokenshaNo"
