mapper.select:
SELECT
dbt5221Nintei."ninteiChosaYoteiYMD" AS DbT5221_ninteiChosaYoteiYMD,
dbt5221Nintei."ninteiChosaYoteiKaishiTime" AS DbT5221_ninteiChosaYoteiKaishiTime,
dbt5221Nintei."ninteiChosaYoteiShuryoTime" AS DbT5221_ninteiChosaYoteiShuryoTime,
dbt5221Nintei."ninteiChosaJikanWaku" AS DbT5221_ninteiChosaJikanWaku,
dbt5221Nintei."biko" AS DbT5221_biko,
dbt5221Nintei."yoyakuKaoFlag" AS DbT5221_yoyakuKaoFlag
FROM
rgdb."DbT5221NinteichosaSchedule" AS dbt5221Nintei 
WHERE
dbt5221Nintei."ninteiChosaYoteiYMD" = #{ninteiChosaYoteiYMD}
AND 
dbt5221Nintei."chosaChikuCode" = #{chosaChikuCode}
AND 
dbt5221Nintei."ninteiChosaItakusakiCode" = #{ninteiChosaItakusakiCode}
AND 
dbt5221Nintei."ninteiChosainCode" = #{ninteiChosainCode}
AND 
dbt5221Nintei."shichosonCode" = #{shichosonCode}
AND
dbt5221Nintei."ninteiChosaYoteiKaishiTime" = #{ninteiChosaYoteiKaishiTime}
AND
dbt5221Nintei."ninteiChosaYoteiShuryoTime" = #{ninteiChosaYoteiShuryoTime}
AND
dbt5221Nintei."ninteiChosaJikanWaku" = #{ninteiChosaJikanWaku}
