mapper.select:
SELECT ChikuShichoson."chosaChikuCode" AS "chikuShichoson_chosaChikuCode" FROM rgdb."DbT5224ChikuShichoson" AS ChikuShichoson WHERE ChikuShichoson."jiChikuFlag" = true
AND ChikuShichoson."shichosonCode" = #{shichosonCode}
GROUP BY ChikuShichoson."chosaChikuCode", ChikuShichoson."yusenNo" ORDER BY ChikuShichoson."yusenNo"

SELECT ChikuNinteiChosain."ninteiChosainCode" AS "chikuNinteiChosain_ninteiChosainCode", ChikuNinteiChosain."shichosonCode" AS "chikuNinteiChosain_shichosonCode", ChosainJoho."chosainShimei" AS "chosainJoho_chosainShimei", NinteichosaSchedule."ninteiChosaYoteiKaishiTime" AS "ninteichosaSchedule_ninteiChosaYoteiKaishiTime", NinteichosaSchedule."ninteiChosaYoteiShuryoTime" AS "ninteichosaSchedule_ninteiChosaYoteiShuryoTime", NinteichosaSchedule."ninteiChosaJikanWaku" AS "ninteichosaSchedule_ninteiChosaJikanWaku" FROM rgdb."DbT5223ChikuNinteiChosain" AS ChikuNinteiChosain LEFT JOIN rgdb."DbT5913ChosainJoho" AS ChosainJoho ON ChosainJoho."shichosonCode" = ChikuNinteiChosain."shichosonCode" AND ChosainJoho."ninteiChosaItakusakiCode" = ChikuNinteiChosain."ninteiChosaItakusakiCode" AND ChosainJoho."ninteiChosainCode" = ChikuNinteiChosain."ninteiChosainCode" LEFT JOIN rgdb."DbT5221NinteichosaSchedule" AS NinteichosaSchedule ON NinteichosaSchedule."ninteiChosaYoteiYMD" = #{設定日} AND NinteichosaSchedule."ninteiChosaItakusakiCode" = ChikuNinteiChosain."ninteiChosaItakusakiCode" AND NinteichosaSchedule."ninteiChosainCode" = ChikuNinteiChosain."ninteiChosainCode" AND NinteichosaSchedule."shichosonCode" = ChikuNinteiChosain."shichosonCode"
AND NinteichosaSchedule."yoyakuKaoFlag" = 'true' AND NinteichosaSchedule."yoyakuJokyo" = '0'
WHERE ChikuNinteiChosain."chosaChikuCode" = #{chosaChikuCode} AND ChikuNinteiChosain."ninteiChosaItakusakiCode" = #{ninteiChosaItakusakiCode} AND ChikuNinteiChosain."shichosonCode" = #{shichosonCode}

SELECT
        NinteichosaSchedule."shinseishoKanriNo" AS "ninteichosaSchedule_shinseishoKanriNo"
FROM 
        rgdb."DbT5221NinteichosaSchedule" AS NinteichosaSchedule
WHERE NinteichosaSchedule."chosaChikuCode" = #{chosaChikuCode}
        AND NinteichosaSchedule."shichosonCode" = #{shichosonCode}
        AND NinteichosaSchedule."ninteiChosaItakusakiCode" = #{ninteiChosaItakusakiCode}
        AND NinteichosaSchedule."ninteiChosainCode" = #{ninteiChosainCode}
        AND NinteichosaSchedule."ninteiChosaYoteiYMD" = #{設定日}
        AND NinteichosaSchedule."ninteiChosaYoteiKaishiTime" = #{ninteiChosaYoteiKaishiTime}
        AND NinteichosaSchedule."ninteiChosaYoteiShuryoTime" = #{ninteiChosaYoteiShuryoTime}
        AND NinteichosaSchedule."ninteiChosaJikanWaku" = #{ninteiChosaJikanWaku}
