AND #{基準日FROM} <= T5201."ninteichosaIraiYMD"
AND T5201."ninteichosaIraiYMD" <= #{基準日TO}
AND #{基準日FROM} <= T5202."ninteichosaJisshiYMD"
AND T5202."ninteichosaJisshiYMD" <= #{基準日TO}
AND #{基準日FROM} <= T5202."ninteichosaJuryoYMD"
AND T5202."ninteichosaJuryoYMD" <= #{基準日TO}
AND T7051."shichosonCode" = #{保険者}
AND
