mapper.select:
WITH rireki AS( SELECT CASE WHEN(lag(T5101."hihokenshaNo",1) OVER (ORDER BY T5101."hihokenshaNo" ASC, T5101."ninteiShinseiYMD" DESC) = T5101."hihokenshaNo") THEN FALSE ELSE TRUE END AS chokkin , T5101."shinseishoKanriNo" , T5101."shoKisaiHokenshaNo" , T5101."hihokenshaNo" , T5101."hihokenshaKana" , T5101."hihokenshaName" , T5101."jusho" , T5910."jigyoshaMeisho" , T5913."chosainShimei" , T5201."ninteichosaIraiYMD" , T5101."ninteiShinseiShinseijiKubunCode" , mod(row_number()over(ORDER BY T5101."hihokenshaNo" ASC,T5101."ninteiShinseiYMD" DESC),25) FROM rgdb."DbT5101NinteiShinseiJoho" T5101 LEFT JOIN ( SELECT T5121."zenkaiShinseishoKanriNo" , T5101_2."hihokenshaNo" FROM rgdb."DbT5121ShinseiRirekiJoho" T5121 INNER JOIN rgdb."DbT5101NinteiShinseiJoho" T5101_2 ON T5101_2."shinseishoKanriNo" = T5121."zenkaiShinseishoKanriNo" ) D ON D."zenkaiShinseishoKanriNo" = T5101."shinseishoKanriNo" INNER JOIN rgdb."DbT5201NinteichosaIraiJoho" AS T5201 ON T5101."shinseishoKanriNo" = T5201."shinseishoKanriNo" AND T5201."logicalDeletedFlag" = False AND NOT EXISTS ( SELECT 'X' FROM rgdb."DbT5201NinteichosaIraiJoho" AS B1 WHERE T5201."shinseishoKanriNo" = B1."shinseishoKanriNo" AND B1."logicalDeletedFlag" = False AND T5201."ninteichosaIraiRirekiNo" < B1."ninteichosaIraiRirekiNo" ) LEFT JOIN rgdb."DbT5910NinteichosaItakusakiJoho" T5910 ON T5201."ninteichosaItakusakiCode" = T5910."ninteichosaItakusakiCode" AND T5101."shichosonCode" = T5910."shichosonCode" AND T5910."jokyoFlag" = True LEFT JOIN rgdb."DbT5913ChosainJoho" T5913 ON T5201."ninteichosaItakusakiCode" = T5913."ninteiChosaItakusakiCode" AND T5201."ninteiChosainCode" = T5913."ninteiChosainCode" AND T5101."shichosonCode" = T5913."shichosonCode" AND T5913."jokyoFlag" = True INNER JOIN (
) AS pickout ON pickout."hihokenshaNo" = T5101."hihokenshaNo" AND pickout."shoKisaiHokenshaNo" = T5101."shoKisaiHokenshaNo" WHERE T5101."logicalDeletedFlag" = FALSE ORDER BY T5101."hihokenshaNo" ASC , T5101."ninteiShinseiYMD" DESC )
SELECT CASE WHEN "chokkin" THEN '直近' ELSE '' END AS "直近区分" ,CASE WHEN "chokkin" OR mod = '1' THEN "shinseishoKanriNo" ELSE '' END AS "shinseishoKanriNo" ,CASE WHEN "chokkin" OR mod = '1' THEN "shoKisaiHokenshaNo" ELSE '' END AS "shoKisaiHokenshaNo" ,CASE WHEN "chokkin" OR mod = '1' THEN "hihokenshaNo" ELSE '' END AS "hihokenshaNo" ,CASE WHEN "chokkin" OR mod = '1' THEN "hihokenshaKana" ELSE '' END AS "hihokenshaKana" ,CASE WHEN "chokkin" OR mod = '1' THEN "hihokenshaName" ELSE '' END AS "hihokenshaName" ,CASE WHEN "chokkin" OR mod = '1' THEN "jusho" ELSE '' END AS "jusho" , "jigyoshaMeisho" AS "jigyoshaMeisho" , "chosainShimei" AS "chosainShimei" , "ninteichosaIraiYMD" AS "ninteichosaIraiYMD" , "ninteiShinseiShinseijiKubunCode" AS "ninteiShinseiShinseijiKubunCode" FROM rireki
