SELECT COUNT(0) FROM (rgdb."DbT5101NinteiShinseiJoho" AS DbT5101 INNER JOIN rgdb."DbT5051KoseiShichosonMaster" AS DbT5051 ON DbT5051."shoKisaiHokenshaNo" = DbT5101."shoKisaiHokenshaNo" INNER JOIN ( SELECT T5201."shinseishoKanriNo" MAX(T5201."ninteichosaIraiRirekiNo") FROM rgdb."DbT5201NinteichosaIraiJoho" AS T5201 WHERE T5201."logicalDeletedFlag" = FALSE GROUP BY T5201."shinseishoKanriNo" )AS TB1 ON TB1."shinseishoKanriNo" = DbT5101."shinseishoKanriNo" INNER JOIN rgdb."DbT5201NinteichosaIraiJoho" AS DbT5201 ON DbT5201."shinseishoKanriNo" = DbT5101."shinseishoKanriNo" AND DbT5201."ninteichosaIraiRirekiNo" = TB1.max
AND DbT5201."ninteichosaTokusokuYMD" BETWEEN #{temp_insatuKikanKaishibi} AND #{temp_insatuKikanShuryoubi}
AND DbT5201."ninteichosaTokusokuYMD" >= #{temp_insatuKikanKaishibi}
AND DbT5201."ninteichosaTokusokuYMD" <= #{temp_insatuKikanShuryoubi}
INNER JOIN rgdb."DbT5105NinteiKanryoJoho" AS DbT5105 ON DbT5105."shinseishoKanriNo" = DbT5101."shinseishoKanriNo" AND DbT5105."ninteiShinseiJohoTorokuKanryoYMD" != '' AND DbT5105."ninteichosaIraiKanryoYMD" != '' AND DbT5105."ninteichosaKanryoYMD" = '' AND DbT5105."ninteiShinsakaiWariateKanryoYMD" = '' AND DbT5105."ninteiShinsakaiKanryoYMD" = '' AND DbT5105."centerSoshinYMD" = '') AS TB2 WHERE 1=1
AND TB2."ninteichosaIraiRirekiNo" = #{temp_hokennshaCode}
AND TB2."ninteiChosaItakusakiCode" = #{temp_ninteiChosaItakusakiCode}
AND TB2."ninteiChosainCode" = #{temp_ninteiChosainCode}
AND TB2."shoriJotaiKubun" != '却下' AND TB2."shoriJotaiKubun" != '取下'
