mapper.select:
SELECT dbT5594ShinsakaiIinJoho.*, dbT5595KaigoNinteiShinsakaiIinShozokuKikanJoho.* FROM ( SELECT
FROM rgdb."DbT5594ShinsakaiIinJoho" WHERE "shinsakaiIinCode"=#{shinsakaiIinCode} ) AS dbT5594ShinsakaiIinJoho LEFT JOIN ( SELECT
FROM rgdb."DbT5595KaigoNinteiShinsakaiIinShozokuKikanJoho" )AS dbT5595KaigoNinteiShinsakaiIinShozokuKikanJoho ON dbT5594ShinsakaiIinJoho."dbT5594ShinsakaiIinJoho_shinsakaiIinCode"=dbT5595KaigoNinteiShinsakaiIinShozokuKikanJoho."dbT5595KaigoNinteiShinsakaiIinShozokuKikanJoho_shinsakaiIinCode" ;

SELECT COUNT(0)
FROM rgdb."DbT5594ShinsakaiIinJoho" DbT5594
WHERE DbT5594."shinsakaiIinCode" = #{shinsakaiIinCode}
