mapper.select:
SELECT
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_shichosonCode";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_shujiiIryokikanCode";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_iryokikanCode";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_iryoKikanMeisho";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_iryoKikanMeishoKana";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_yubinNo";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_jusho";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_telNo";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_faxNo";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_daihyoshaName";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_daihyoshaNameKana";
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_jokyoFlag";
DbT7051KoseiShichosonMaster."DbT7051KoseiShichosonMaster_shichosonMeisho"
FROM
() AS DbT5911ShujiiIryoKikanJoho
INNER JOIN
() AS DbT7051KoseiShichosonMaster
ON
DbT7051KoseiShichosonMaster."dbT7051KoseiShichosonMaster_shichosonCode"  =  DbT5911ShujiiIryoKikanJoho."dbT5911ShujiiIryoKikanJoho_shichosonCode" 

    1 = 1

        AND DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_jokyoFlag" = TRUE


        AND DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_jokyoFlag" = FALSE


        AND DbT5911ShujiiIryoKikanJoho."dbT5911ShujiiIryoKikanJoho_shichosonCode" = #{市町村コード}


        AND DbT5911ShujiiIryoKikanJoho."dbT5911ShujiiIryoKikanJoho_shujiiIryokikanCode" >= #{主治医医療機関コードFrom}


        AND DbT5911ShujiiIryoKikanJoho."dbT5911ShujiiIryoKikanJoho_shujiiIryokikanCode" <= #{主治医医療機関コードTo}


        AND DbT5911ShujiiIryoKikanJoho."dbT5911ShujiiIryoKikanJoho_iryoKikanMeisho" LIKE '%' || #{医療機関名称} || '%'


        AND DbT5911ShujiiIryoKikanJoho."dbT5911ShujiiIryoKikanJoho_iryoKikanMeishoKana" LIKE '%' || #{医療機関カナ名称} || '%'


ORDER BY
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_shichosonCode",
DbT5911ShujiiIryoKikanJoho."DbT5911ShujiiIryoKikanJoho_shujiiIryokikanCode"

        LIMIT #{saidaiHyojiKensu}
