mapper.select:
SELECT C.転入_被保険者番号 as 転入_被保険者番号, C.転入_異動日 as 転入_異動日, C.転入_枝番 as 転入_枝番, C.転入_市町村コード as 転入_市町村コード, C.転入_識別コード AS 転入_識別コード, C.転出_被保険者番号 AS 転出_被保険者番号, C.転出_異動日 AS 転出_異動日, C.転出_枝番 AS 転出_枝番, C.転出_市町村コード AS 転出_市町村コード, C.転出_識別コード AS 転出_識別コード, 転出PSM."jutonaiJusho" AS 転出PSM_住登内住所, 転出PSM."jutonaiBanchi" AS 転出PSM_住登内番地, 転出PSM."jutonaiKatagaki" AS 転出PSM_住登内方書, 転出PSM."tenshutsuYoteiIdoYMD" AS 転出PSM_転出予定異動年月日, 転出PSM."tenshutsuKakuteiIdoYMD" AS 転出PSM_転出確定異動年月日, 転出PSM."tenshutsuKakuteiTsuchiYMD" AS 転出PSM_転出確定通知年月日, 転入PSM."kanaMeisho" AS 転入PSM_カナ名称, 転入PSM."meisho" AS 転入PSM_名称, 転入PSM."jutonaiJusho" AS 転入PSM_住登内住所, 転入PSM."jutonaiBanchi" AS 転入PSM_住登内番地, 転入PSM."jutonaiKatagaki" AS 転入PSM_住登内方書, 転入PSM."torokuIdoYMD" AS 転入PSM_登録異動年月日, 転入PSM."torokuTodokedeYMD" AS 転入PSM_登録異動届出日 FROM ( SELECT A.転入_被保険者番号 AS 転入_被保険者番号, A.転入_異動日 AS 転入_異動日, A.転入_枝番 AS 転入_枝番, A.転入_市町村コード AS 転入_市町村コード, A.転入_識別コード AS 転入_識別コード, B."hihokenshaNo" AS 転出_被保険者番号, B."idoYMD" AS 転出_異動日, B."edaNo" AS 転出_枝番, B."shichosonCode" AS 転出_市町村コード, B."shikibetsuCode" AS 転出_識別コード, ROW_NUMBER() OVER(PARTITION BY A."転入_被保険者番号", A."転入_異動日",A."転入_枝番" ORDER BY B."idoYMD" DESC,B."edaNo" DESC) AS 順序番号 FROM( SELECT 転入."hihokenshaNo" AS 転入_被保険者番号, 転入."idoYMD" AS 転入_異動日, 転入."edaNo" AS 転入_枝番, 転入."shichosonCode" AS 転入_市町村コード, 転入."shikibetsuCode" AS 転入_識別コード FROM rgdb."DbT1001HihokenshaDaicho" AS 転入 WHERE 転入."idoJiyuCode" = '11' and 転入."logicalDeletedFlag" = FALSE
AND 転入."shichosonCode"=#{市町村コード}
AND to_char(転入."insertTimestamp",'YYYYMMDD')>= #{日付From} AND to_char(転入."insertTimestamp",'YYYYMMDD') <= #{日付To}
AND to_char(転入."insertTimestamp",'YYYYMMDD') <= #{日付To}
AND to_char(転入."insertTimestamp",'YYYYMMDD') >= #{日付From}
ORDER BY 転入."hihokenshaNo" ASC, 転入."idoYMD" DESC, 転入."edaNo" DESC ) AS A LEFT JOIN rgdb."DbT1001HihokenshaDaicho" AS B ON A.転入_被保険者番号=B."hihokenshaNo" AND A.転入_異動日> B."idoYMD" AND B."shikibetsuCode"!= A.転入_識別コード ) AS C LEFT JOIN AS 転出PSM ON 転出PSM."shikibetsuCode"=C.転出_識別コード AND 転出PSM."idoYMD" = C.転出_異動日 LEFT JOIN AS 転入PSM ON 転入PSM."shikibetsuCode"=C.転入_識別コード AND 転入PSM."idoYMD" = C.転入_異動日 WHERE C.順序番号 = '1' ORDER BY 転入_被保険者番号 ASC, 転入_異動日 DESC, 転入_枝番 DESC
