mapper.select:
SELECT DISTINCT jigyoHokokuTokeiData."hokokuYSeireki" AS "hokokuYSeireki", jigyoHokokuTokeiData."hokokuM" AS "hokokuM", jigyoHokokuTokeiData."shukeiTaishoYSeireki" AS "shukeiTaishoYSeireki", jigyoHokokuTokeiData."shukeiTaishoM" AS "shukeiTaishoM", jigyoHokokuTokeiData."toukeiTaishoKubun" AS "toukeiTaishoKubun", jigyoHokokuTokeiData."shichosonCode" AS "shichosonCode", jigyoHokokuTokeiData."hyoNo" AS "hyoNo", jigyoHokokuTokeiData."shukeiNo" AS "shukeiNo" FROM rgdb."DbT7021JigyoHokokuTokeiData" AS jigyoHokokuTokeiData WHERE jigyoHokokuTokeiData."isDeleted" = false AND jigyoHokokuTokeiData."hokokuM" = '00'
AND jigyoHokokuTokeiData."hokokuYSeireki" = #{hokokuYSeireki}
AND jigyoHokokuTokeiData."toukeiTaishoKubun" = #{hokensilyakbn}
AND ( ( jigyoHokokuTokeiData."hyoNo" = '01' AND jigyoHokokuTokeiData."shukeiNo" = '0400' ) OR( jigyoHokokuTokeiData."hyoNo" = '02' AND jigyoHokokuTokeiData."shukeiNo" IN ('0601', '0602', '0603') ) OR( jigyoHokokuTokeiData."hyoNo" = '08' AND jigyoHokokuTokeiData."shukeiNo" IN ('0100', '0200') ) )
AND ( ( jigyoHokokuTokeiData."hyoNo" = '11' AND jigyoHokokuTokeiData."shukeiNo" = '0400' ) OR( jigyoHokokuTokeiData."hyoNo" = '12' AND jigyoHokokuTokeiData."shukeiNo" IN ('0601', '0602', '0603') ) OR( jigyoHokokuTokeiData."hyoNo" = '18' AND jigyoHokokuTokeiData."shukeiNo" IN ('0100', '0200') ) )
AND jigyoHokokuTokeiData."shichosonCode" = #{shichosonCode}
ORDER BY jigyoHokokuTokeiData."shichosonCode" ASC, jigyoHokokuTokeiData."hokokuYSeireki" DESC, jigyoHokokuTokeiData."shukeiNo" ASC
