FROM
rgdb."DbT7021JigyoHokokuTokeiData" AS DbT7021
WHERE
DbT7021."hokokuYSeireki" = #{過去集計年月_年}
AND DbT7021."hokokuM" = #{過去集計年月_月}
AND DbT7021."shukeiTaishoYSeireki" = #{一般状況1_10集計年月_年}
AND DbT7021."shukeiTaishoM" = #{一般状況1_10集計年月_月}
