mapper.select:
SELECT
FROM rgdb."DbT1001HihokenshaDaicho" AS DbT1001
WHERE DbT1001."logicalDeletedFlag" = FALSE AND DbT1001."shikakuShutokuYMD" <> DbT1001."shikakuSoshitsuYMD" AND #{対象開始日時} < DbT1001."lastUpdateTimestamp" AND DbT1001."lastUpdateTimestamp" <= #{対象終了日時}
WHERE DbT1001."logicalDeletedFlag" = FALSE AND DbT1001."shikakuShutokuYMD" <> DbT1001."shikakuSoshitsuYMD" AND DbT1001."hihokenshaNo" IN ( SELECT DISTINCT DbT1001."hihokenshaNo" FROM rgdb."DbT1001HihokenshaDaicho" AS DbT1001 WHERE #{対象開始日時} < DbT1001."lastUpdateTimestamp" AND DbT1001."lastUpdateTimestamp" <= #{対象終了日時} UNION ALL SELECT DISTINCT DbT7304."hihokenshaNo" FROM rgdb."DbT7304TokuteiKojinJohoTeikyoKanri" AS DbT7304 WHERE DbT7304."dataSetNo" = #{データセット番号} AND( DbT7304."teikyoKubun" = #{提供区分_個人番号未設定により未提供} OR DbT7304."teikyoKubun" = #{提供区分_その他エラーにより未提供} ) )
WHERE DbT1001."logicalDeletedFlag" = FALSE AND DbT1001."shikakuShutokuYMD" <> DbT1001."shikakuSoshitsuYMD" AND DbT1001."hihokenshaNo" = #{個人番号付替対象者被保険者番号}
ORDER BY DbT1001."hihokenshaNo", DbT1001."idoYMD", DbT1001."edaNo"
