mapper.select:
SELECT
tbl."jigyoshaName" as "dbT7060KaigoJigyosha_jigyoshaName"
FROM (
SELECT row_number() over() as rownum,
"jigyoshaName"
FROM
rgdb."DbT7060KaigoJigyosha"
WHERE
rgdb."DbT7060KaigoJigyosha"."jigyoshaNo" = #{jigyoshaNo}
AND rgdb."DbT7060KaigoJigyosha"."yukoKaishiYMD" <= #{yukoKaishiYMD}
AND rgdb."DbT7060KaigoJigyosha"."yukoShuryoYMD" > #{yukoKaishiYMD}
)tbl
WHERE tbl."rownum" = 1
