mapper.select:
SELECT A.* FROM ( SELECT , row_number() OVER ( PARTITION BY "DbT1001HihokenshaDaicho"."hihokenshaNo" ORDER BY "DbT1001HihokenshaDaicho"."idoYMD" DESC , "DbT1001HihokenshaDaicho"."edaNo" DESC ) AS rrkno FROM rgdb."DbT1001HihokenshaDaicho" WHERE "DbT1001HihokenshaDaicho"."hihokennshaKubunCode" = '1' AND "DbT1001HihokenshaDaicho"."logicalDeletedFlag" = false ) AS A WHERE A.rrkno = 1
AND A."dbT1001HihokenshaDaicho_hihokenshaNo" = #{hihokenshaNo}
ORDER BY A."dbT1001HihokenshaDaicho_idoYMD" DESC
