SELECT DbT1001Hiho. "hihokenshaNo" as "DbT1001Hiho_hihokenshaNoD", DbT1001Hiho."idoYMD" as "DbT1001Hiho_idoYMD", DbT1001Hiho."edaNo" as "DbT1001Hiho_edaNo", DbT1001Hiho."shikakuHenkoYMD" as "DbT1001Hiho_shikakuHenkoYMD", DbT1001Hiho."shikakuHenkoTodokedeYMD" as "DbT1001Hiho_shikakuHenkoTodokedeYMD", DbT1001Hiho."jushochitokureiTekiyoJiyuCode" as "DbT1001Hiho_jushochitokureiTekiyoJiyuCode", DbT7051KoseiShicho."shichosonCode" as "DbT7051KoseiShicho_shichosonCode", DbT7051KoseiShicho."shichosonMeisho" as "DbT7051KoseiShicho_shichosonMeisho", DbT7051Kosei."shichosonCode" as "DbT7051Kosei_koikinaiTokureiSochimotoShichosonCode", DbT7051Kosei."shichosonMeisho" as "DbT7051Kosei_shichosonMeisho2", DbT7056Gappei."kyuShichosonCode" as "DbT7056Gappei_kyuShichosonCode", DbT7056Gappei."kyuShichosonMeisho" as "DbT7056Gappei_kyuShichosonMeisho", DbT1001Hiho."lastUpdateTimestamp" as "DbT1001Hiho_lastUpdateTimestamp"
FROM rgdb."DbT1001HihokenshaDaicho" as DbT1001Hiho LEFT JOIN rgdb ."DbT7051KoseiShichosonMaster" as DbT7051KoseiShicho ON DbT1001Hiho."kyuShichosonCode" = DbT7051KoseiShicho."shichosonCode" AND DbT7051KoseiShicho."gappeiKyuShichosonKubun" = '0' LEFT JOIN rgdb."DbT7051KoseiShichosonMaster" as DbT7051Kosei ON DbT1001Hiho. "koikinaiTokureiSochimotoShichosonCode" = DbT7051Kosei."shichosonCode" AND DbT7051Kosei."gappeiKyuShichosonKubun" = '0' LEFT JOIN rgdb."DbT7056GappeiShichoson" as DbT7056Gappei ON DbT1001Hiho."kyuShichosonCode" = DbT7056Gappei."kyuShichosonCode"
WHERE
DbT1001Hiho."hihokenshaNo" = #{hihokenshaNo}
